where.if:
and (o.orderNumber in (
select og.orderNumber from
b2b_order_goods og where goodsInfo like '%${goodsInfo}%'
) or o.orderNumber like '%${goodsInfo}%'
)

mapper.select:
SELECT o.orderNumber, o.orderClassify, o.purchaserPk, o.supplierPk, o.orderStatus, o.orderAmount, o.actualAmount, o.insertTime, o.meno, o.memberPk, o.memberName, o.storeName, o.storePk, o.paymentType, o.paymentName, o.source, o.paymentTime, o.logisticsModelPk, o.logisticsModelName, o.purchaseType, o.isDelete, o.isDeleteSp, o.employeeNumber, o.employeeName, o.employeePk, o.economicsGoodsType, o.economicsGoodsName, o.paymentType, o.logisticsModelPk as logisticsModelType, o.deliverDetails, o.purchaserInfo, o.supplierInfo, o.addressInfo, o.block FROM b2b_order o
order by o.insertTime desc
limit #{start},#{limit}

SELECT ifnull(o.orderNumber, '') AS orderNumber, ifnull(og.childOrderNumber, '') AS childOrderNumber, ifnull(o.addressInfo, '') AS addressInfo, ifnull(o.purchaserInfo, '') AS purchaserInfo, ifnull(o.supplierInfo, '') AS supplierInfo, ifnull(og.goodsInfo, '') AS goodsInfo, ifnull(o.storeName, '') AS storeName, o.block, CASE o.orderStatus WHEN 0 THEN '待审核' WHEN 1 THEN '待付款' WHEN 2 THEN '待确认' WHEN 3 THEN '待发货' WHEN 4 THEN '已发货' WHEN 5 THEN '部分发货' WHEN 6 THEN '已完成' WHEN - 1 THEN '已取消' END AS orderStatusName, CASE o.purchaseType WHEN 1 THEN '自采' WHEN 2 THEN '代采' WHEN 3 THEN '后台代采' END AS purchaseTypeName, IFNULL(og.boxesShipped, 0) AS boxesShipped, IFNULL(og.weightShipped, 0) AS weightShipped,
IF ( ( og.afterBoxes - IFNULL(og.boxesShipped, 0) ) < 0, 0, ( og.afterBoxes - IFNULL(og.boxesShipped, 0) ) ) AS boxesNoShipped,
IF ( ( og.afterWeight - IFNULL(og.weightShipped, 0) ) < 0, 0, ( og.afterWeight - IFNULL(og.weightShipped, 0) ) ) AS weightNoShipped, ifnull(og.originalPrice, '') AS originalPrice, ifnull(og.presentPrice, '') AS presentPrice, ROUND( (presentPrice) * IF ( afterWeight > 0, afterWeight, weight ), 2 ) AS presentTotalPrice, ifnull(og.presentTotalFreight, '') AS presentTotalFreight, o.insertTime, o.actualAmount, ifnull(o.paymentName, '') AS paymentName, CASE o.source WHEN 1 THEN 'PC' WHEN 2 THEN 'WAP' WHEN 3 THEN 'APP' WHEN 4 THEN '后台' END AS sourceName, ifnull(o.memberName, '') AS memberName, ifnull(o.meno, '') AS meno, ifnull(o.employeeNumber, '') AS employeeNumber, ifnull(o.employeeName, '') AS employeeName, o.economicsGoodsType, ifnull(o.economicsGoodsName, '') AS economicsGoodsName, ifnull(o.deliverDetails, '') AS deliverDetails, og.afterBoxes, og.afterWeight, og.weight, og.boxes, og.otherDiscount FROM b2b_order o LEFT JOIN b2b_order_goods og ON o.orderNumber = og.orderNumber WHERE 1 = 1
AND o.employeePk=#{employeePk}
AND o.orderClassify=#{orderClassify}
AND o.purchaserPk = #{purchaserPk}
AND o.supplierPk = #{supplierPk}
AND o.storePk=#{storePk}
AND o.purchaseType = #{purchaseType}
AND o.orderNumber like '%${orderNumber}%'
AND (o.orderStatus = 4 or o.orderStatus = 5)
AND o.orderStatus = #{orderStatus}
AND o.paymentType = #{paymentType}
AND o.memberPk = #{memberPk}
AND (o.employeeName like '%${memberName}%' or o.employeeNumber like '%${memberName}%')
AND o.source = #{source}
AND o.isDelete = #{isDelete}
AND o.isDeleteSp = #{isDeleteSp}
and o.purchaserInfo like '%contactsTel%${contactsTel}%'
and o.purchaserInfo like '%purchaserName%${purchaserName}%'
AND deliverDetails like '%${deliverDetails}%'
AND str_to_date(o.insertTime, '%Y-%m-%d') >= #{insertTimeBegin}
AND str_to_date(o.insertTime, '%Y-%m-%d') <= #{insertTimeEnd}
AND str_to_date( o.paymentTime, '%Y-%m-%d') >= #{paymentStartTime}
AND str_to_date( o.paymentTime, '%Y-%m-%d') <= #{paymentEndTime}
and og.goodsInfo like '%${goodsInfo}%'
AND o.purchaserPk in
#{item}
order by o.insertTime desc
